import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';


class Jumbotron extends Component {
    render() {
        const hasTitle = props.title != null;
        const hasDescription = props.description != null;
        var rslt = null;
        if (hasTitle)
        {
            rslt += <section className="jumbotron text-center flex-fill"> <div className="container"> <h1 className="jumbotron-heading">{this.props.title}</h1>;
              if (hasDescription) {
                rslt += <p className="lead">{this.props.description}</p>
              }
              rslt += </div>
              rslt += </section>
        }
        return rslt;
    }
}

export default Jumbotron;
